import { CartService } from 'src/app/services/cart.service';
import { Component} from '@angular/core';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  /**
   *
   */
  public products: any = []
  public grandTotal: number = 0;

  constructor(private cartService : CartService) {

  }

  ngOnInit(): void {
    this.cartService.getProducts()
    .subscribe(res =>{
      this.products = res;
      this.grandTotal = this.cartService.getTotalPrice();
    })
  }
  removeItem(item : any){
   this.cartService.removeCartItem(item)
  }
  emptyCart(){
    this.cartService.removeAllCart();
  }
}

<ng-container *ngIf="products.length !=0">
    <div class="cart-container" >
        <div class="cart-item" *ngFor="let item of products; let i = index">
          <img class="item-image" src="{{item.image}}" alt="Ürün Resmi">
          <div class="item-details">
            <div class="item-name">{{item.title}}</div>
            <div class="item-price">${{item.price}}</div>
            <div class="item-quantity">Quantity: {{item.quantity}} </div>
          </div>
          <button class="remove-button" (click)="removeItem(item)" ><i class="fa fa-times"></i></button>
        </div>
        <div class="total-price d-flex"><b>Total: ${{grandTotal}}</b></div>
      </div>
      <button routerLink="/products" class="btn btn-outline-primary btn-shop">Shop now</button>
</ng-container>


<ng-container *ngIf="products.length ==0">
    <div class="container">
        <div class="card">
            <h5 class="card-title">My Cart</h5>
        </div>
        <div class="center">
            <img src="/assets/fcart.png" alt="">
            <h4>Cart is empty!</h4>
            <h6>Add item to it now</h6>
            <button routerLink="/products" class="btn btn-outline-primary">Shop now</button>
        </div>
    </div>
</ng-container>
